$(document).ready(function(){$('[data-pages="search"]').search({searchField:"#overlay-search",closeButton:".overlay-close",suggestions:"#overlay-suggestions",brand:".brand",onSearchSubmit:function(e){console.log("Search for: "+e)},onKeyEnter:function(e){console.log("Live search for: "+e);var a=$("#overlay-search"),t=$(".search-results");clearTimeout($.data(this,"timer")),t.fadeOut("fast");var o=setTimeout(function(){t.find(".result-name").each(function(){0!=a.val().length&&($(this).html(a.val()),t.fadeIn("fast"))})},500);$(this).data("timer",o)}})});